mapper.insert:
insert into bxt_table
(
	  col1, col2, col3
)
values(
	#{col1, jdbcType=VARCHAR}
	, #{col2, jdbcType=VARCHAR}
	, #{col3, jdbcType=VARCHAR}
)

mapper.update:
update
	bxt_table
set
	col2 = #{col2, jdbcType=VARCHAR}
	, col3 = #{col3, jdbcType=VARCHAR}
where
	col1 = #{col1, jdbcType=VARCHAR}

mapper.delete:
delete from bxt_table
where 
	col1 = #{col1, jdbcType=VARCHAR}
